import React, {Component} from 'react'
import firebase from "firebase";
import "firebase/storage";


class Firefind extends Component {
  style = {
    borderBottom:"1px solid gray"
  }

  // 初期化。ステートとイベント用メソッドの設定
  constructor(props) {
    super(props);
    this.state = {
      input:'',
      data:[]
    }
    this.doChange = this.doChange.bind(this);
    this.doAction = this.doAction.bind(this);
  }

  // 入力フィールドに入力時の処理
  doChange(e){
    this.setState({
      input:e.target.value
    })
  }

  // ボタンクリック時の処理
  doAction(e){
    this.findFireData(this.state.input);
  }

  // 検索の実行
  findFireData(s){
    let db = firebase.database();
    let ref = db.ref('Sample/');
    let self = this;
    ref.orderByKey()
      .equalTo(s)
      .on('value', (snapshot)=>{
        self.setState({
          data:snapshot.val()
        });
      });
  }

  テーブルの内容の作成
  getTableData(){
    let result = [];
    if (this.state.data == nul || this.state.data.length == 0){
      return [<tr key="0"><th>NO DATA.</th></tr>];
    }
    for(let i in this.state.data){
      result.push(
        <tr key={i}>
          <th>{this.state.data[i].ID}</th>
          <th>{this.state.data[i].name}</th>
          <th>{this.state.data[i].message}</th>
        </tr>
      );
    }
    return result;
  }


  // レンダリング
  rebder(){
    <input type="text" onChange={this.doChange}
      style={this.style} value={this.state,input} />

  }
}
